pragma solidity ^0.8.13;

/**
 * @title A collection of defined structs
 * @notice This library defines the various data models that the Sarcophagus
 * system uses
 */
library LibTypes {
    // DoesNotExist must come first on the list to be the default value
    enum SarcophagusState {
        DoesNotExist,
        Active,
        Resurrecting,
        Resurrected,
        Buried,
        Cleaned,
        Accused,
        Failed
    }

    // A struct of just the signature. This is used primarily by the
    // finalizeSarcophagus function for the arweave archaeologist. Note that,
    // unlike the regular archaeologists, the sarcophagus already stores the
    // single arweave archaeologist's address so there is no need to pass in the
    // address to the finalizeSarcophagus function.
    struct Signature {
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    // Signature struct created to make passing in the signature argmuments into
    // finalizedSarcophagus easier and to avoid the "stack too deep" error.
    // Also attaching arachaeologist addresses so we can tie the signature back
    // to the address in finalizeSarcophagus.
    struct SignatureWithAccount {
        address account;
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    // SelectedArchaeologistData is the struct that is passed into the
    // initializeSarcophagus function. Even though we don't need each storage
    // fee of the archaeologist, the storage fee is included in the struct to
    // reduce the stack size within the function, preventing the "stack too
    // deep" error.
    struct SelectedArchaeologistData {
        address archAddress;
        uint256 diggingFee;
        bytes32 unencryptedShardDoubleHash;
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    // ArchaeologistStorage is the struct that is stored in AppStorage under the
    // sarcophagusArchaeologists mapping.
    //
    // The archaeologist address is left out since each archaeologist's address
    // is stored on the sarcophagus object as an array.
    //
    // The storage fee is left out because we only need to store the storage fee
    // of the archaeologist uploading to arweave, which will be stored directly
    // on the sarcophagus.
    struct ArchaeologistStorage {
        uint256 diggingFee;
        uint256 diggingFeesPaid;
        bytes32 unencryptedShardDoubleHash;
        bytes unencryptedShard;
    }

    // ArchaeologistProfile is used to store archaeologist profile data
    struct ArchaeologistProfile {
        bool exists;
        string peerId;
        uint256 minimumDiggingFee;
        uint256 maximumRewrapInterval;
        uint256 freeBond;
        uint256 cursedBond;
    }

    struct SarcophagusMemory {
        string name;
        address recipient;
        uint256 resurrectionTime;
        uint256 maximumRewrapInterval;
        bool canBeTransferred;
        uint8 minShards;
        uint256 timestamp;
    }

    // The ArchaeologistStorage struct could be contained in this Sarcophagus
    // struct as a mapping, but it was put into it's own mapping
    // (sarcophagusArchaeologists) directly in AppStorage. Instead the
    // sarcophagus stores the addresses of each archaeologist added to it. This
    // was done to simplify the creation of a sarcophagus object in
    // initializeSarcophagus.
    struct Sarcophagus {
        string name;
        SarcophagusState state;
        bool canBeTransferred;
        uint8 minShards;
        uint256 resurrectionTime;
        uint256 maximumRewrapInterval;
        string[] arweaveTxIds;
        address embalmer;
        address recipientAddress;
        address[] archaeologists;
    }

    struct MetadataAttributes {
        string sarcophagusName;
        uint256 diggingFee;
        uint256 resurrectionTime;
        uint256 diggingFeesPaid;
    }

    // Only used in the ViewStateFacet to return statistics data.
    // Contains a list of sarcoIds for each category. We could simply return the counts of the
    // arrays but we are already storing the lists of sarcoIds so we may as well use them.
    struct ArchaeologistStatistics {
        uint256 successes;
        uint256 accusals;
        uint256 cleanups;
    }
}
","keccak256":"0x1ebf49a2c2223309fc8975dfac35ffd03daf5746458d8e93b33fe86fad73ae1f","license":"Unlicense"},"contracts/storage/LibAppStorage.sol":{"content":"// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../libraries/LibTypes.sol";

// Global storage for the app. Can be accessed in facets and in libraries
struct AppStorage {
    IERC20 sarcoToken;
    // The percentage (i.e. 1 = 1%) of a sarcophagus' total digging fees that will be collected on
    // createSarcophagus and rewrapSarcophagus, paid by the embalmer
    uint256 protocolFeeBasePercentage;
    // The amount of protocol fees currently stored on the contract
    uint256 totalProtocolFees;
    // grace period an archaeologist is given to resurrect a sarcophagus after the resurrection time
    uint256 gracePeriod;
    // threshold after which archaeologist signatures on sarcophagus params expire and the sarcophagus must be renegotiated
    uint256 expirationThreshold;
    // sarcophagi
    bytes32[] sarcophagusIdentifiers;
    // archaeologist profiles
    address[] archaeologistProfileAddresses;
    mapping(address => LibTypes.ArchaeologistProfile) archaeologistProfiles;

    // archaeologistSarcoSuccesses is needed by the clean function
    // to lookup whether an archaeologist has completed an unwrapping
    mapping(address => mapping(bytes32 => bool)) archaeologistSarcoSuccesses;

    // Archaeologist reputation statistics
    mapping(address => bytes32[]) archaeologistSuccesses;
    mapping(address => bytes32[]) archaeologistAccusals;
    mapping(address => bytes32[]) archaeologistCleanups;

    // Track how much archaeologists have made. To be credited and debited
    // as archaeologists fulfill their duties and withdraw their rewards
    mapping(address => uint256) archaeologistRewards;
    mapping(bytes32 => LibTypes.Sarcophagus) sarcophagi;
    // sarcophagus ownerships
    mapping(address => bytes32[]) embalmerSarcophagi;
    mapping(address => bytes32[]) archaeologistSarcophagi;
    mapping(address => bytes32[]) recipientSarcophagi;
    // Mapping of unencrypted shard double hashes to archaeologists who are
    // responsible for them. Needed to optimise Accuse algo - unencrypted shard is
    // double hashed and used as a constant O(1) lookup here
    mapping(bytes32 => address) doubleHashedShardArchaeologists;
    // A mapping used to store an archaeologist's data on a sarcophagus.
    // Digging fees, storage fees, and the hashed shards of the
    // archaeologists all need to be stored per sarcophagus. This mapping of a
    // mapping stores the archaeologist's data we need per sarcophagus.
    // Example usage (to retrieve the digging fees an archaeologist may claim on some sarcophagus):
    //   LibTypes.ArchaeologistStorage bondedArchaeologist = sarcophagusArchaeologists[sarcoId][archAddress];
    //   uint256 diggingFees = bondedArchaeologist.diggingFees;
    mapping(bytes32 => mapping(address => LibTypes.ArchaeologistStorage)) sarcophagusArchaeologists;
}

library LibAppStorage {
    function getAppStorage() internal pure returns (AppStorage storage s) {
        // Set the position of our struct in contract storage
        // Since AppStorage s is the first and only state variable declared in
        // facets its position in contract storage is 0
        assembly {
            s.slot := 0
        }
    }
}
