pragma solidity ^0.8.13;

import "../libraries/LibTypes.sol";
import "../libraries/LibUtils.sol";
import "../storage/LibAppStorage.sol";

contract ViewStateFacet {
    /// @notice Gets the total protocol fees from the contract.
    /// @return The total protocol fees
    function getTotalProtocolFees() external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.totalProtocolFees;
    }

    /// @notice Get the protocol fee base percentage from the contract.
    /// @return The protocol fee base percentage - protocolFeeBasePercentage
    function getProtocolFeeBasePercentage() external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.protocolFeeBasePercentage;
    }

    /// @notice Get the cursed bond percentage from the contract.
    /// @return The cursed bond percentage - cursedBondPercentage
    function getCursedBondPercentage() external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.cursedBondPercentage;
    }

    /// @notice Gets archaeologist profiles given a list of archaeologist addresses.
    /// If an invalid address is included, simply leaves it out of the list.
    /// @param addresses The list of archaeologist addresses
    /// @return The list of archaeologist profiles
    function getArchaeologistProfiles(
        address[] memory addresses
    ) external view returns (LibTypes.ArchaeologistProfile[] memory) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibTypes.ArchaeologistProfile[] memory profiles = new LibTypes.ArchaeologistProfile[](
            addresses.length
        );

        for (uint256 i = 0; i < addresses.length; i++) {
            // Skip unregistered archaeologists
            if (s.archaeologistProfiles[addresses[i]].maximumRewrapInterval == 0) {
                continue;
            }
            profiles[i] = s.archaeologistProfiles[addresses[i]];
        }

        return profiles;
    }

    /// @notice Gets the grace period an archaeologist is given to resurrect a sarcophagus after the resurrection time passes
    /// @return The resurrection grace period
    function getGracePeriod() external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.gracePeriod;
    }

    /// @notice Gets the window after end of gracePeriod + resurrectionTime where embalmer can claim remaining bonds from archaeologists that have failed to publish private keys
    /// @return The embalmer claim window
    function getEmbalmerClaimWindow() external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.embalmerClaimWindow;
    }

    /// @notice Gets the expiration threshold after which a sarcophagus must be renegotiated
    /// @return The expiration threshold
    function getExpirationThreshold() external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.expirationThreshold;
    }

    /// @notice Given an archaeologist address, return that archaeologist's
    /// profile
    /// @param archaeologist The archaeologist account's address
    /// @return the Archaeologist object
    function getArchaeologistProfile(
        address archaeologist
    ) external view returns (LibTypes.ArchaeologistProfile memory) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibUtils.revertIfArchProfileDoesNotExist(archaeologist);
        return s.archaeologistProfiles[archaeologist];
    }

    /// @notice Return the list of registereed archaeologist addresses.
    /// @return addresses of registered archaeologists
    function getArchaeologistProfileAddresses() external view returns (address[] memory) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.archaeologistProfileAddresses;
    }

    /// @notice Given an index (of the full archaeologist array), return the
    /// archaeologist address at that index
    /// @param index The index of the registered archaeologist
    /// @return address of the archaeologist
    function getArchaeologistProfileAddressAtIndex(uint256 index) external view returns (address) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.archaeologistProfileAddresses[index];
    }

    /// @notice Returns the amount of free bond stored in the contract for an
    /// archaeologist.
    /// @param archaeologist The address of the archaeologist whose
    /// free bond is being returned
    function getFreeBond(address archaeologist) external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.archaeologistProfiles[archaeologist].freeBond;
    }

    /// @notice Returns the amount of rewards stored in the contract for an
    /// archaeologist.
    /// @param archaeologist The address of the archaeologist whose
    /// reward is being returned
    function getRewards(address archaeologist) external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.archaeologistRewards[archaeologist];
    }

    /// @notice Returns the amount of cursed bond stored in the contract for an
    /// archaeologist.
    /// @param archaeologist The address of the archaeologist whose
    /// cursed bond is being returned
    function getCursedBond(address archaeologist) external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.archaeologistProfiles[archaeologist].cursedBond;
    }

    // Only used in the ViewStateFacet to return statistics data.
    // Contains a list of sarcoIds for each category. We could simply return the counts of the
    // arrays but we are already storing the lists of sarcoIds so we may as well use them.
    struct ArchaeologistStatistics {
        uint256 successes;
        uint256 accusals;
        uint256 failures;
    }

    struct SarcophagusResponse {
        uint256 resurrectionTime;
        uint256 previousRewrapTime;
        bool isCompromised;
        bool isCleaned;
        string name;
        uint8 threshold;
        uint256 maximumRewrapInterval;
        uint256 maximumResurrectionTime;
        uint256 cursedBondPercentage;
        string arweaveTxId;
        address embalmerAddress;
        address recipientAddress;
        address[] archaeologistAddresses;
        uint8 publishedPrivateKeyCount;
        bool hasLockedBond;
    }

    /// @notice Returns data on the sarcophagus with the supplied id
    /// includes aggregate data on cursed archaeologists associated with the sarcophagus
    ///     - publishedPrivateKeyCount - the total number of private keys published by archaeologists on the sarcophagus
    ///     - hasLockedBond - true if archaeologists still have bond locked in the contract for this sarcophagus
    /// @param sarcoId The identifier of the sarcophagus being returned
    function getSarcophagus(bytes32 sarcoId) external view returns (SarcophagusResponse memory) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibTypes.Sarcophagus storage sarcophagus = s.sarcophagi[sarcoId];

        // Confirm sarcophagus exists
        if (sarcophagus.resurrectionTime == 0) {
            revert LibErrors.SarcophagusDoesNotExist(sarcoId);
        }

        uint8 publishedPrivateKeyCount = 0;
        bool hasLockedBond = false;
        for (uint256 i = 0; i < sarcophagus.cursedArchaeologistAddresses.length; i++) {
            // archaeologist has published a private key
            if (
                sarcophagus
                    .cursedArchaeologists[sarcophagus.cursedArchaeologistAddresses[i]]
                    .privateKey != 0
            ) {
                publishedPrivateKeyCount++;
            } else if (
                !sarcophagus
                    .cursedArchaeologists[sarcophagus.cursedArchaeologistAddresses[i]]
                    .isAccused &&
                !sarcophagus.isCompromised &&
                !sarcophagus.isCleaned &&
                sarcophagus.resurrectionTime != 2 ** 256 - 1
            ) {
                // if the sarcophagus is not compromised, buried, or cleaned and
                // one or more unaccused archaeologists hasn't published a private key there is locked bond on the sarcophagus
                hasLockedBond = true;
            }
        }

        return
            SarcophagusResponse({
                resurrectionTime: sarcophagus.resurrectionTime,
                previousRewrapTime: sarcophagus.previousRewrapTime,
                isCompromised: sarcophagus.isCompromised,
                isCleaned: sarcophagus.isCleaned,
                name: sarcophagus.name,
                threshold: sarcophagus.threshold,
                maximumRewrapInterval: sarcophagus.maximumRewrapInterval,
                maximumResurrectionTime: sarcophagus.maximumResurrectionTime,
                cursedBondPercentage: sarcophagus.cursedBondPercentage,
                arweaveTxId: sarcophagus.arweaveTxId,
                embalmerAddress: sarcophagus.embalmerAddress,
                recipientAddress: sarcophagus.recipientAddress,
                archaeologistAddresses: sarcophagus.cursedArchaeologistAddresses,
                publishedPrivateKeyCount: publishedPrivateKeyCount,
                hasLockedBond: hasLockedBond
            });
    }

    /// @notice Given an embalmer's address, returns the identifiers of all
    /// sarcophagi that the embalmer has created.
    /// @param embalmer The address of the embalmer whose sarcophagi are being
    /// returned
    function getEmbalmerSarcophagi(address embalmer) external view returns (bytes32[] memory) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.embalmerSarcophagi[embalmer];
    }

    /// @notice Given an archaeologist's address, returns the identifiers of all
    /// sarcophagi that the archaeologist has participated in.
    /// @param archaeologist The address of the archaeologist whose sarcophagi
    /// are being returned
    function getArchaeologistSarcophagi(
        address archaeologist
    ) external view returns (bytes32[] memory) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.archaeologistSarcophagi[archaeologist];
    }

    /// @notice Given a recipient's address, returns the identifiers of all
    /// sarcophagi that the recipient has participated in.
    /// @param recipient The address of the recipient whose sarcophagi are being
    /// returned
    function getRecipientSarcophagi(address recipient) external view returns (bytes32[] memory) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.recipientSarcophagi[recipient];
    }

    /// @notice Returns the data stored on a sarcophagus for an archaeologist.
    /// @param sarcoId The identifier of the sarcophagus whose data is being
    /// returned
    /// @param archaeologist The address of the archaeologist whose data is
    /// being returned
    function getSarcophagusArchaeologist(
        bytes32 sarcoId,
        address archaeologist
    ) external view returns (LibTypes.CursedArchaeologist memory) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.sarcophagi[sarcoId].cursedArchaeologists[archaeologist];
    }
}
","keccak256":"0x7a99fb82fffe3f10efa64020f0c5f347cbbf18d08751dcb1fb23777270c354d2","license":"Unlicense"},"contracts/libraries/LibBonds.sol":{"content":"// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

import "../storage/LibAppStorage.sol";
import "../libraries/LibTypes.sol";
import {LibErrors} from "./LibErrors.sol";

import "../facets/EmbalmerFacet.sol";

library LibBonds {
    /// @notice Decreases the amount stored in the freeBond mapping for an
    /// archaeologist. Reverts if the archaeologist's free bond is lower than
    /// the amount.
    /// @param archaeologist The address of the archaeologist whose
    /// free bond is being decreased
    /// @param amount The amount to decrease the free bond by
    function decreaseFreeBond(address archaeologist, uint256 amount) internal {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // Revert if the amount is greater than the current free bond
        if (amount > s.archaeologistProfiles[archaeologist].freeBond) {
            revert LibErrors.NotEnoughFreeBond(
                s.archaeologistProfiles[archaeologist].freeBond,
                amount
            );
        }

        // Decrease the free bond amount
        s.archaeologistProfiles[archaeologist].freeBond -= amount;
    }

    /// @notice Decreases the amount stored in the cursedBond mapping for an
    /// archaeologist, without respectively increasing their free bond.
    /// @param archaeologist The address of the archaeologist
    /// @param amount The amount to slash
    function decreaseCursedBond(address archaeologist, uint256 amount) internal {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // Revert if the amount is greater than the current cursed bond
        if (amount > s.archaeologistProfiles[archaeologist].cursedBond) {
            revert LibErrors.NotEnoughCursedBond(
                s.archaeologistProfiles[archaeologist].cursedBond,
                amount
            );
        }

        s.archaeologistProfiles[archaeologist].cursedBond -= amount;
    }

    /// @notice Bonds the archaeologist to a sarcophagus.
    /// This does the following:
    ///   - adds the archaeologist's curse params and address to the sarcophagus
    ///   - calculates digging fees to be locked and later paid to archaeologist (includes curseFee)
    ///   - locks this amount from archaeologist's free bond; increases cursedBond by same
    ///   - Adds the sarcophagus' id to the archaeologist's record of bonded sarcophagi
    /// @param sarcoId Id of the sarcophagus with which to curse the archaeologist
    /// @param archaeologist The archaeologist to curse, with associated parameters of the curse
    ///
    /// @return the amount of digging fees due the embalmer for this curse
    function curseArchaeologist(
        bytes32 sarcoId,
        EmbalmerFacet.CurseParams calldata archaeologist,
        uint256 index
    ) internal returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibTypes.Sarcophagus storage sarcophagus = s.sarcophagi[sarcoId];

        sarcophagus.cursedArchaeologists[archaeologist.archAddress] = LibTypes.CursedArchaeologist({
            publicKey: archaeologist.publicKey,
            privateKey: 0,
            isAccused: false,
            diggingFeePerSecond: archaeologist.diggingFeePerSecond,
            curseFee: archaeologist.curseFee
        });
        sarcophagus.cursedArchaeologistAddresses[index] = archaeologist.archAddress;

        // Calculate digging fees due for this time period (creationTime/previousRewrapTime -> resurrectionTime)
        uint256 diggingFeesDue = (archaeologist.diggingFeePerSecond *
            (sarcophagus.resurrectionTime - sarcophagus.previousRewrapTime))
            + archaeologist.curseFee;

        // Use cursed bond percentage to determine how much bond to lock up
        uint256 bondToCurse = (((diggingFeesDue) * s.cursedBondPercentage) / 100);

        // Transfer bond to curse from free bond to cursed bond
        decreaseFreeBond(archaeologist.archAddress, bondToCurse);
        s.archaeologistProfiles[archaeologist.archAddress].cursedBond += bondToCurse;

        s.archaeologistSarcophagi[archaeologist.archAddress].push(sarcoId);

        return diggingFeesDue;
    }

    /// @notice Calculates and unlocks an archaeologist's cursed bond. Pays due digging fees to the archaeologist.
    /// @param sarcoId the identifier of the sarcophagus to free the archaeologist from
    /// @param archaeologistAddress the address of the archaeologist to free
    function freeArchaeologist(bytes32 sarcoId, address archaeologistAddress) internal {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibTypes.Sarcophagus storage sarcophagus = s.sarcophagi[sarcoId];

        LibTypes.CursedArchaeologist storage cursedArchaeologist = s
            .sarcophagi[sarcoId]
            .cursedArchaeologists[archaeologistAddress];

        // Calculate the digging fees to be paid since the last rewrap (or creation)
        uint256 diggingFeeAmount = cursedArchaeologist.diggingFeePerSecond *
            (sarcophagus.resurrectionTime - sarcophagus.previousRewrapTime);

        // If sarcophagus has not be been rewrapped yet, pay out the curseFee
        if (!sarcophagus.isRewrapped) {
            diggingFeeAmount += cursedArchaeologist.curseFee;
        }

        uint256 cursedBondAmount = (diggingFeeAmount * sarcophagus.cursedBondPercentage) / 100;

        LibBonds.decreaseCursedBond(archaeologistAddress, cursedBondAmount);
        s.archaeologistProfiles[archaeologistAddress].freeBond += cursedBondAmount;

        s.archaeologistRewards[archaeologistAddress] += diggingFeeAmount;
    }
}
","keccak256":"0xd1118d784e09390789c4ebcf46056d86bee393da39fa77fc6f3bb02fb50b5eb9","license":"Unlicense"},"contracts/libraries/LibErrors.sol":{"content":"// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

/**
 * @title A collection of Errors
 * @notice This library defines all of the Errors that the Sarcophagus system
 * uses.
 */
library LibErrors {
    error ArchaeologistNotOnSarcophagus(address archaeologist);

    error NotEnoughCursedBond(uint256 cursedBond, uint256 amount);

    error NotEnoughFreeBond(uint256 freeBond, uint256 amount);

    error ArchaeologistProfileExistsShouldBe(bool exists, address archaeologist);

    error SarcophagusDoesNotExist(bytes32 sarcoId);

    error SarcophagusInactive(bytes32 sarcoId);

    error SarcophagusCompromised(bytes32 sarcoId);

    error SenderNotEmbalmer(address sender, address embalmer);

    error InvalidSignature(
        // address recovered from signature via ecrecover
        address recoveredAddress,
        // address we expected to have signed the data
        address expectedAddress
    );
}
","keccak256":"0xe2e2e99c7e608571b8d072ceacc037b744ca637ad66633af1c2f10e935eb8567","license":"Unlicense"},"contracts/libraries/LibTypes.sol":{"content":"// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

/**
 * @title Types shared across facets for the Sarcophagus diamond
 */
library LibTypes {
    struct Sarcophagus {
        // Also used for existence checks -- does not exist if 0
        uint256 resurrectionTime;
        uint256 previousRewrapTime;
        // todo: run gas cost evaluation on storing isCompromised vs looping through stored archaeologists and checking isAccused
        bool isCompromised;
        bool isCleaned;
        bool isRewrapped;
        uint8 threshold;
        string name;
        uint256 maximumRewrapInterval;
        uint256 maximumResurrectionTime;
        string arweaveTxId;
        address embalmerAddress;
        address recipientAddress;
        address[] cursedArchaeologistAddresses;
        mapping(address => CursedArchaeologist) cursedArchaeologists;
        uint256 cursedBondPercentage;
    }

    struct CursedArchaeologist {
        uint256 diggingFeePerSecond;
        // Also used for publish checks -- has not published if 0
        bytes32 privateKey;
        // Also used for curse checks -- is not bonded if length is 0
        bytes publicKey;
        bool isAccused;
        uint256 curseFee;
    }

    struct Signature {
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    struct ArchaeologistProfile {
        // Also used for existence checks -- does not exist if 0
        uint256 maximumRewrapInterval;
        uint256 maximumResurrectionTime;
        string peerId;
        uint256 minimumDiggingFeePerSecond;
        uint256 freeBond;
        uint256 cursedBond;
        uint256 curseFee;
    }
}
","keccak256":"0x1d0b42de0dec8ddf3d1569747f4e5facda08be16f45cbe698764006f8b13af5e","license":"Unlicense"},"contracts/libraries/LibUtils.sol":{"content":"// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

import "../storage/LibAppStorage.sol";
import "../libraries/LibTypes.sol";
import {LibErrors} from "../libraries/LibErrors.sol";
import "../facets/ThirdPartyFacet.sol";
import "./LibTypes.sol";
import "../facets/EmbalmerFacet.sol";

/**
 * @title Utility functions used within the Sarcophagus system
 * @notice This library implements various functions that are used throughout
 * Sarcophagus, mainly to DRY up the codebase
 * @dev these functions are all stateless, public, pure/view
 */
library LibUtils {
    /**
     * @notice The archaeologist needs to sign off on two pieces of data
     * to guarantee their unrwap will be successful
     *
     * @param agreedMaximumRewrapInterval that the archaeologist has agreed to for the sarcophagus
     * @param timestamp that the archaeologist has agreed to for the sarcophagus
     * @param curseParams parameters of curse signed by archaeologist
     */
    function verifyArchaeologistSignature(
        uint256 agreedMaximumRewrapInterval,
        uint256 maximumResurrectionTime,
        uint256 timestamp,
        EmbalmerFacet.CurseParams calldata curseParams
    ) internal pure {
        // Hash the hash of the data payload
        bytes32 messageHash = keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(
                    abi.encode(
                        curseParams.publicKey,
                        agreedMaximumRewrapInterval,
                        maximumResurrectionTime,
                        curseParams.diggingFeePerSecond,
                        timestamp,
                        curseParams.curseFee
                    )
                )
            )
        );

        // Generate the address from the signature.
        // ecrecover should always return a valid address.
        address recoveredAddress = ecrecover(
            messageHash,
            curseParams.v,
            curseParams.r,
            curseParams.s
        );

        if (recoveredAddress != curseParams.archAddress) {
            revert LibErrors.InvalidSignature(recoveredAddress, curseParams.archAddress);
        }
    }

    /// @notice Verifies that a signature and public key were created from the same private key
    /// @param sarcoId the sarcoId that was signed
    /// @param paymentAddress the payment address that was signed
    /// @param publicKey an uncompressed 65 byte secp256k1 public key
    /// @param signature signature on the sarco id and payment address
    /// @return true if the signature was signed by the private key corresponding to the supplied public key
    function verifyAccusalSignature(
        bytes32 sarcoId,
        address paymentAddress,
        bytes calldata publicKey,
        LibTypes.Signature calldata signature
    ) internal pure returns (bool) {
        // removes the 0x04 prefix from an uncompressed public key
        bytes memory truncatedPublicKey = new bytes(publicKey.length - 1);
        for (uint256 i = 1; i < publicKey.length; i++) {
            truncatedPublicKey[i - 1] = publicKey[i];
        }
        bytes32 messageHash = keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(abi.encode(sarcoId, paymentAddress))
            )
        );
        // Use ecrecover to get the address that signed the message
        address signingAddress = ecrecover(messageHash, signature.v, signature.r, signature.s);

        address publicKeyAddress = address(
            uint160(
                uint256(keccak256(truncatedPublicKey)) &
                    0x00FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF
            )
        );

        return signingAddress == publicKeyAddress;
    }

    /// @notice Checks if an archaeologist profile exists and
    /// reverts if so
    ///
    /// @param archaeologist the archaeologist address to check existence of
    function revertIfArchProfileExists(address archaeologist) internal view {
        AppStorage storage s = LibAppStorage.getAppStorage();

        if (s.archaeologistProfiles[archaeologist].maximumRewrapInterval != 0) {
            revert LibErrors.ArchaeologistProfileExistsShouldBe(false, archaeologist);
        }
    }

    /// @notice Checks if an archaeologist profile doesn't exist and reverts if so
    ///
    /// @param archaeologist the archaeologist address to check lack of existence of
    function revertIfArchProfileDoesNotExist(address archaeologist) internal view {
        AppStorage storage s = LibAppStorage.getAppStorage();

        if (s.archaeologistProfiles[archaeologist].maximumRewrapInterval == 0) {
            revert LibErrors.ArchaeologistProfileExistsShouldBe(true, archaeologist);
        }
    }

    /// @notice Calculates the protocol fees to be taken from the embalmer.
    /// @param totalDiggingFees to be paid. Protocol fee is a percentage of this
    /// @return The protocol fees amount
    function calculateProtocolFees(uint256 totalDiggingFees) internal view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();

        return (totalDiggingFees * s.protocolFeeBasePercentage) / 100;
    }
}
