// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../libraries/LibTypes.sol";
import {LibErrors} from "../libraries/LibErrors.sol";
import {LibBonds} from "../libraries/LibBonds.sol";
import {LibUtils} from "../libraries/LibUtils.sol";
import {AppStorage} from "../storage/LibAppStorage.sol";

contract ThirdPartyFacet {
    AppStorage internal s;

    event AccuseArchaeologist(
        bytes32 indexed sarcoId,
        address indexed accuser,
        uint256 accuserBondReward,
        uint256 embalmerBondReward
    );

    event CleanUpSarcophagus(
        bytes32 indexed sarcoId,
        address indexed cleaner,
        uint256 cleanerBondReward,
        uint256 embalmerBondReward
    );

    /// @notice Close a sarcophagus that has not been unwrapped within its grace period
    /// @param sarcoId The identifier of the sarcophagus to clean
    /// @param paymentAddress The address to which rewards will be sent
//    function clean(bytes32 sarcoId, address paymentAddress) external {
//        LibUtils.revertIfNotExistOrInactive(sarcoId);
//
//        LibTypes.Sarcophagus storage sarco = s.sarcophagi[sarcoId];
//
//        // Make sure the sarco is cleanable
//        if (block.timestamp < s.gracePeriod + sarco.resurrectionTime) {
//            revert LibErrors.SarcophagusNotCleanable();
//        }
//
//        // Figure out which archaeoligists did not fulfil their duties;
//        // accumulate their digging fees
//        address[] memory archAddresses = sarco.archaeologists;
//
//        uint256 totalDiggingFee;
//
//        for (uint256 i = 0; i < archAddresses.length; i++) {
//            // todo: consider skipping this mapping and just retrieving the keyshares
//            bool didNotUnwrap = s.archaeologistSarcoSuccesses[archAddresses[i]][sarcoId] == false;
//
//            if (didNotUnwrap) {
//                LibTypes.ArchaeologistStorage memory defaulter = s.sarcophagusArchaeologists[
//                    sarcoId
//                ][archAddresses[i]];
//
//                totalDiggingFee += defaulter.diggingFee;
//
//                // decrease the defaulter's cursed bond
//                LibBonds.decreaseCursedBond(archAddresses[i], defaulter.diggingFee);
//
//                // Save the failure to unwrap against the archaeologist
//                s.archaeologistCleanups[archAddresses[i]].push(sarcoId);
//            }
//        }
//
//        (uint256 cleanerBondReward, uint256 embalmerBondReward) = _distributeLoot(
//            paymentAddress,
//            sarco,
//            totalDiggingFee
//        );
//
//        sarco.state = LibTypes.SarcophagusState.Cleaned;
//
//        emit CleanUpSarcophagus(sarcoId, msg.sender, cleanerBondReward, embalmerBondReward);
//    }

    /**
     * @notice Accuse one or more archaeologists of leaking key shares by submitting the hashes of the leaked shares
     * If the archaeologists responsible for those shares haven't already been accused, their locked bond will be
     * split between the embalmer and the supplied payment address and digging fees allocated for those archaeologists will be refunded to the embalmer
     *
     * If k or more archaeologists are accused over the lifetime of a sarcophagus, the sarcophagus state will be updated to Accused
     * and bonds for all remaining unaccused archaeologists will be returned
     *
     * @param sarcoId The identifier of the sarcophagus having leaked shares
     * @param keyShareHashes hashes of the leaked key shares
     * @param paymentAddress the address to which rewards should be sent if successful
     */
    function accuse(
        bytes32 sarcoId,
        bytes32[] calldata keyShareHashes,
        address paymentAddress
    ) external {
        LibTypes.Sarcophagus storage sarcophagus = s.sarcophagi[sarcoId];

        // Confirm sarcophagus exists
        if (sarcophagus.resurrectionTime == 0) {
            revert LibErrors.SarcophagusDoesNotExist(sarcoId);
        }

        // verify that current time is not past resurrection time
        if (block.timestamp > sarcophagus.resurrectionTime) {
            revert LibErrors.SarcophagusIsUnwrappable();
        }

        // Confirm the sarcophagus has not been compromised
        if (sarcophagus.isCompromised) {
            revert LibErrors.SarcophagusCompromised(sarcoId);
        }

        // Confirm the sarcophagus is not buried
        if (sarcophagus.resurrectionTime == 2**256 - 1) {
            revert LibErrors.SarcophagusInactive(sarcoId);
        }

        // build an array of the addresses of the archaeologists currently being accused
        address[] memory accusedArchAddresses = new address[](keyShareHashes.length);

        // track the combined locked bond across all archaeologists being accused in this call
        // locked bond will be equal to the amount of diggingFees allocated by the embalmer to pay the archaeologist
        uint256 totalDiggingFees = 0;
        uint256 accusalCount = 0;
        for (uint256 i = 0; i < keyShareHashes.length; i++) {
            // generate the double hash of the key share
            bytes32 doubleHashedKeyShare = keccak256(abi.encode(keyShareHashes[i]));

            // look up the archaeologist responsible for the key share
            address accusedArchaeologistAddress = s.doubleHashedShardArchaeologists[doubleHashedKeyShare];
            LibTypes.CursedArchaeologist storage accusedArchaeologist = sarcophagus.cursedArchaeologists[accusedArchaeologistAddress];

            // verify the accused archaeologist is cursed on the sarcophagus
            if (accusedArchaeologist.doubleHashedKeyShare == 0) {
                revert LibErrors.ArchaeologistNotOnSarcophagus(msg.sender);
            }

            // if the archaeologist has already been accused on this sarcophagus break without taking action
            if (accusedArchaeologist.isAccused) {
                break;
            }

            // mark the archaeologist on the sarcophagus as having been accused
            accusedArchaeologist.isAccused = true;
            accusedArchAddresses[accusalCount++] = accusedArchaeologistAddress;

            // track the sum of all digging fees for all accused archaeologists
            totalDiggingFees += accusedArchaeologist.diggingFee;

            // slash the accused archaeologist's bond
            LibBonds.decreaseCursedBond(accusedArchaeologistAddress, accusedArchaeologist.diggingFee);

            // Save the accusal against the archaeologist
            s.archaeologistAccusals[accusedArchaeologistAddress].push(sarcoId);
        }

        // if none of the accusals were valid because the archaeologists have all already been accused, return without taking action
        if (accusalCount == 0) {
            return;
        }

        // the sarcophagus is compromised if the current call has successfully accused the sss threshold of archaeologists
        if (accusalCount >= sarcophagus.threshold) {
            sarcophagus.isCompromised = true;
        } else {
            // if the current call hasn't resulted in at least sss threshold archaeologists being accused
            // check if total number of historical accusals on sarcophagus is greater than threshold
            uint256 totalAccusals = 0;
            for (uint256 i = 0; i < sarcophagus.cursedArchaeologistAddresses.length; i++) {
                if (sarcophagus.cursedArchaeologists[sarcophagus.cursedArchaeologistAddresses[i]].isAccused) {
                    totalAccusals++;
                }
            }
            // the sarcophagus is compromised if k or more archaeologists have been accused over the lifetime of the sarcophagus
            if (totalAccusals >= sarcophagus.threshold) {
                sarcophagus.isCompromised = true;
            }
        }

        // if k or more archaeologists have been accused over the lifetime of the sarcophagus, funds should
        // be returned to the remaining well behaved archaeologists
        if (sarcophagus.isCompromised) {
            // iterate through all archaeologist addresses on the sarcophagus
            for (uint256 i = 0; i < sarcophagus.cursedArchaeologistAddresses.length; i++) {
                // if the archaeologist has never been accused, release their locked bond back to them
                if (!sarcophagus.cursedArchaeologists[sarcophagus.cursedArchaeologistAddresses[i]].isAccused) {
                    LibBonds.freeArchaeologist(sarcoId, sarcophagus.cursedArchaeologistAddresses[i]);
                }
            }
        }

        // refund the diggingFees allocated by the embalmer to the accused archaeologists
        // split the total bond being slashed between the embalmer and the payment address
        (uint256 accuserBondReward, uint256 embalmerBondReward) = _distributeLoot(
            paymentAddress,
            sarcophagus,
            totalDiggingFees
        );

        emit AccuseArchaeologist(sarcoId, msg.sender, accuserBondReward, embalmerBondReward);
    }

    /**
     * @notice Takes a sarcophagus's digging fee, splits it in half, and sends
     * to paymentAddress and embalmer
     * @param paymentAddress payment address for the transaction caller
     * @param sarc the sarcophagus to operate on
     * @param totalDiggingFee the sum of digging fees of all archs that failed to fulfil their duties
     * @return halfToSender the amount of SARCO token going to transaction
     * sender
     * @return halfToEmbalmer the amount of SARCO token going to embalmer
     */
    function _distributeLoot(
        address paymentAddress,
        LibTypes.Sarcophagus storage sarc,
        uint256 totalDiggingFee
    ) private returns (uint256, uint256) {
        // split the sarcophagus's cursed bond into two halves
        uint256 halfToEmbalmer = totalDiggingFee / 2;
        uint256 halfToSender = totalDiggingFee - halfToEmbalmer;

        // transfer the cursed half, plus digging fee to the
        // embalmer
        s.sarcoToken.transfer(sarc.embalmerAddress, totalDiggingFee + halfToEmbalmer);

        // transfer the other half of the cursed bond to the transaction caller
        s.sarcoToken.transfer(paymentAddress, halfToSender);

        return (halfToSender, halfToEmbalmer);
    }

    function _hashHelper(bytes memory data) private pure returns (bytes32) {
        return keccak256(data);
    }
}

contracts/facets/ViewStateFacet.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

import "../libraries/LibTypes.sol";
import "hardhat/console.sol";
import {AppStorage} from "../storage/LibAppStorage.sol";

contract ViewStateFacet {
    AppStorage internal s;

    /// @notice Gets the total protocol fees from the contract.
    /// @return The total protocol fees
    function getTotalProtocolFees() external view returns (uint256) {
        return s.totalProtocolFees;
    }

    /// @notice Get the protocol fee base percentage from the contract.
    /// @return The protocol fee base percentage - protocolFeeBasePercentage
    function getProtocolFeeBasePercentage() external view returns (uint256) {
        return s.protocolFeeBasePercentage;
    }

    /// @notice Gets archaeologist profiles given a list of archaeologist addresses.
    /// If an invalid address is included, simply leaves it out of the list.
    /// @param addresses The list of archaeologist addresses
    /// @return The list of archaeologist profiles
    function getArchaeologistProfiles(address[] memory addresses)
        external
        view
        returns (LibTypes.ArchaeologistProfile[] memory)
    {
        LibTypes.ArchaeologistProfile[] memory profiles = new LibTypes.ArchaeologistProfile[](
            addresses.length
        );

        for (uint256 i = 0; i < addresses.length; i++) {
            // Check that the archaeologist profile exists
            if (!s.archaeologistProfiles[addresses[i]].exists) {
                continue;
            }
            profiles[i] = s.archaeologistProfiles[addresses[i]];
        }

        return profiles;
    }

    /// @notice Gets the grace period an archaeologist is given to resurrect a sarcophagus after the resurrection time passes
    /// @return The resurrection grace period
    function getGracePeriod() external view returns (uint256) {
        return s.gracePeriod;
    }

    /// @notice Gets the expiration threshold after which a sarcophagus must be renegotiated
    /// @return The expiration threshold
    function getExpirationThreshold() external view returns (uint256) {
        return s.expirationThreshold;
    }

    /// @notice Given an archaeologist address, return that archaeologist's
    /// profile
    /// @param archaeologist The archaeologist account's address
    /// @return the Archaeologist object
    function getArchaeologistProfile(address archaeologist)
        external
        view
        returns (LibTypes.ArchaeologistProfile memory)
    {
        return s.archaeologistProfiles[archaeologist];
    }

    /// @notice Return the list of registereed archaeologist addresses.
    /// @return addresses of registered archaeologists
    function getArchaeologistProfileAddresses() external view returns (address[] memory) {
        return s.archaeologistProfileAddresses;
    }

    /// @notice Given an index (of the full archaeologist array), return the
    /// archaeologist address at that index
    /// @param index The index of the registered archaeologist
    /// @return address of the archaeologist
    function getArchaeologistProfileAddressAtIndex(uint256 index) external view returns (address) {
        return s.archaeologistProfileAddresses[index];
    }

    /// @notice Returns the amount of free bond stored in the contract for an
    /// archaeologist.
    /// @param archaeologist The address of the archaeologist whose
    /// free bond is being returned
    function getFreeBond(address archaeologist) external view returns (uint256) {
        return s.archaeologistProfiles[archaeologist].freeBond;
    }

    /// @notice Returns the amount of rewards stored in the contract for an
    /// archaeologist.
    /// @param archaeologist The address of the archaeologist whose
    /// reward is being returned
    function getRewards(address archaeologist) external view returns (uint256) {
        return s.archaeologistRewards[archaeologist];
    }

    /// @notice Returns the amount of cursed bond stored in the contract for an
    /// archaeologist.
    /// @param archaeologist The address of the archaeologist whose
    /// cursed bond is being returned
    function getCursedBond(address archaeologist) external view returns (uint256) {
        return s.archaeologistProfiles[archaeologist].cursedBond;
    }

    /// @notice Returns whether an archaeologist completed an unwrap for a sarcophagus
    /// @param archaeologist The address of the archaeologist
    /// @param sarcoId the sarcophagus to check if unwrapping occured
    function getArchaeologistSuccessOnSarcophagus(address archaeologist, bytes32 sarcoId)
        external
        view
        returns (bool)
    {
        return s.sarcophagi[sarcoId].cursedArchaeologists[archaeologist].rawKeyShare.length != 0;
    }

    /// @notice Returns the number of successful unwraps for an archaeologist.
    /// @param archaeologist The address of the archaeologist whose success
    //  count is being returned
    function getArchaeologistSuccessesCount(address archaeologist) external view returns (uint256) {
        return s.archaeologistSuccesses[archaeologist].length;
    }

    /// @notice Returns the sarcophagus unique identifier for a given
    /// archaeologist and index of the successfully unwrapped sarcophagi
    /// @param archaeologist The address of an archaeologist
    /// @param index The index of the archaeologist's unwrapped sarcophagi
    /// @return the identifier associated with the index of the archaeologist's
    /// unwrapped sarcophagi
    function archaeologistSuccessesIdentifier(address archaeologist, uint256 index)
        external
        view
        returns (bytes32)
    {
        return s.archaeologistSuccesses[archaeologist][index];
    }

    /// @notice Returns the number of accusations for an archaeologist.
    /// @param archaeologist The address of the archaeologist whose accusations
    /// count is being returned
    function getArchaeologistAccusalsCount(address archaeologist) external view returns (uint256) {
        return s.archaeologistAccusals[archaeologist].length;
    }

    /// @notice Returns the sarcophagus unique identifier for a given
    /// archaeologist and index of the accused sarcophagi
    /// @param archaeologist The address of an archaeologist
    /// @param index The index of the archaeologist's accused sarcophagi
    /// @return the identifier associated with the index of the archaeologist's
    /// accused sarcophagi
    function archaeologistAccusalsIdentifier(address archaeologist, uint256 index)
        external
        view
        returns (bytes32)
    {
        return s.archaeologistAccusals[archaeologist][index];
    }

    /// @notice Returns the number of cleanups for an archaeologist.
    /// @param archaeologist The address of the archaeologist whose cleanups
    /// count is being returned
    function getArchaeologistCleanupsCount(address archaeologist) external view returns (uint256) {
        return s.archaeologistCleanups[archaeologist].length;
    }

    /// @notice Returns the sarcophagus unique identifier for a given
    /// archaeologist and index of the cleaned-up sarcophagi
    /// @param archaeologist The address of an archaeologist
    /// @param index The index of the archaeologist's cleaned-up sarcophagi
    /// @return the identifier associated with the index of the archaeologist's
    /// cleaned-up sarcophagi
    function archaeologistCleanupsIdentifier(address archaeologist, uint256 index)
        external
        view
        returns (bytes32)
    {
        return s.archaeologistCleanups[archaeologist][index];
    }

    // Only used in the ViewStateFacet to return statistics data.
    // Contains a list of sarcoIds for each category. We could simply return the counts of the
    // arrays but we are already storing the lists of sarcoIds so we may as well use them.
    struct ArchaeologistStatistics {
        uint256 successes;
        uint256 accusals;
        uint256 cleanups;
        uint256 failures;
    }

    /// @notice Gets all reputation statistics for each archaeologist
    /// Contains a list of counts for each category.
    /// @param addresses The list of archaeologist addresses
    /// @return The list of archaeologist statistics
    function getArchaeologistsStatistics(address[] memory addresses)
        external
        view
        returns (ArchaeologistStatistics[] memory)
    {
        ArchaeologistStatistics[]
            memory statsList = new ArchaeologistStatistics[](addresses.length);

        for (uint256 i = 0; i < addresses.length; i++) {
            // Count the failures for the arch
            uint256 failures = 0;

            // Get arch sarco ids
            bytes32[] storage sarcoIds = s.archaeologistSarcophagi[addresses[i]];

            // For each sarco id, if the sarco id is not included in successes and resurrection time
            // has passed, it's a failure
            for (uint j = 0; j < sarcoIds.length; j++) {
                LibTypes.Sarcophagus storage sarco = s.sarcophagi[sarcoIds[j]];
                if (
                    !this.getArchaeologistSuccessOnSarcophagus(addresses[i], sarcoIds[j]) &&
                    block.timestamp > sarco.resurrectionTime + s.gracePeriod
                ) {
                    failures += 1;
                }
            }

            statsList[i] = ArchaeologistStatistics(
                this.getArchaeologistSuccessesCount(addresses[i]),
                this.getArchaeologistAccusalsCount(addresses[i]),
                this.getArchaeologistCleanupsCount(addresses[i]),
                failures
            );
        }

        return statsList;
    }

    struct SarcophagusResponse {
        uint256 resurrectionTime;
        bool isCompromised;
        string name;
        uint8 threshold;
        uint256 maximumRewrapInterval;
        string[2] arweaveTxIds;
        address embalmerAddress;
        address recipientAddress;
        address[] archaeologistAddresses;
        uint8 publishedKeyShareCount;
        bool hasLockedBond;

    }

    /// @notice Returns data on the sarcophagus with the supplied id
    /// includes aggregate data on cursed archaeologists associated with the sarcophagus
    ///     - publishedKeyShareCount - the total number of keyshares published by archaeologists on the sarcophagus
    ///     - hasLockedBond - true if archaeologists still have bond locked in the contract for this sarcophagus
    /// @param sarcoId The identifier of the sarcophagus being returned
    function getSarcophagus(bytes32 sarcoId) external view returns (SarcophagusResponse memory) {
        LibTypes.Sarcophagus storage sarcophagus = s.sarcophagi[sarcoId];

        uint8 publishedKeyShareCount = 0;
        bool hasLockedBond = false;
        for (uint256 i = 0; i < sarcophagus.cursedArchaeologistAddresses.length; i++) {
            // archaeologist has published a keyshare
            if (sarcophagus.cursedArchaeologists[sarcophagus.cursedArchaeologistAddresses[i]].rawKeyShare.length != 0) {
                publishedKeyShareCount++;
            } else if (!sarcophagus.cursedArchaeologists[sarcophagus.cursedArchaeologistAddresses[i]].isAccused) {
                // if archaeologist hasn't published a key share and is not accused, they still have locked bond
                hasLockedBond = true;
            }
        }

        return SarcophagusResponse({
            resurrectionTime: sarcophagus.resurrectionTime,
            isCompromised: sarcophagus.isCompromised,
            name: sarcophagus.name,
            threshold: sarcophagus.threshold,
            maximumRewrapInterval: sarcophagus.maximumRewrapInterval,
            arweaveTxIds: sarcophagus.arweaveTxIds,
            embalmerAddress: sarcophagus.embalmerAddress,
            recipientAddress: sarcophagus.recipientAddress,
            archaeologistAddresses: sarcophagus.cursedArchaeologistAddresses,
            publishedKeyShareCount:publishedKeyShareCount,
            hasLockedBond: hasLockedBond
        });
    }

    /// @notice Given an embalmer's address, returns the identifiers of all
    /// sarcophagi that the embalmer has created.
    /// @param embalmer The address of the embalmer whose sarcophagi are being
    /// returned
    function getEmbalmerSarcophagi(address embalmer) external view returns (bytes32[] memory) {
        return s.embalmerSarcophagi[embalmer];
    }

    /// @notice Given an archaeologist's address, returns the identifiers of all
    /// sarcophagi that the archaeologist has participated in.
    /// @param archaeologist The address of the archaeologist whose sarcophagi
    /// are being returned
    function getArchaeologistSarcophagi(address archaeologist)
        external
        view
        returns (bytes32[] memory)
    {
        return s.archaeologistSarcophagi[archaeologist];
    }

    /// @notice Given a recipient's address, returns the identifiers of all
    /// sarcophagi that the recipient has participated in.
    /// @param recipient The address of the recipient whose sarcophagi are being
    /// returned
    function getRecipientSarcophagi(address recipient) external view returns (bytes32[] memory) {
        return s.recipientSarcophagi[recipient];
    }

    /// @notice Returns the data stored on a sarcophagus for an archaeologist.
    /// @param sarcoId The identifier of the sarcophagus whose data is being
    /// returned
    /// @param archaeologist The address of the archaeologist whose data is
    /// being returned
    function getSarcophagusArchaeologist(bytes32 sarcoId, address archaeologist)
        external
        view
        returns (LibTypes.CursedArchaeologist memory)
    {
        return s.sarcophagi[sarcoId].cursedArchaeologists[archaeologist];
    }
}

contracts/libraries/LibBonds.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

import "../storage/LibAppStorage.sol";
import "../libraries/LibTypes.sol";
import {LibErrors} from "../libraries/LibErrors.sol";

library LibBonds {
    /// @notice Decreases the amount stored in the freeBond mapping for an
    /// archaeologist. Reverts if the archaeologist's free bond is lower than
    /// the amount.
    /// @param archaeologist The address of the archaeologist whose
    /// free bond is being decreased
    /// @param amount The amount to decrease the free bond by
    function decreaseFreeBond(address archaeologist, uint256 amount) internal {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // Revert if the amount is greater than the current free bond
        if (amount > s.archaeologistProfiles[archaeologist].freeBond) {
            revert LibErrors.NotEnoughFreeBond(
                s.archaeologistProfiles[archaeologist].freeBond,
                amount
            );
        }

        // Decrease the free bond amount
        s.archaeologistProfiles[archaeologist].freeBond -= amount;
    }

    /// @notice Increases the amount stored in the freeBond mapping for an
    /// archaeologist.
    /// @param archaeologist The address of the archaeologist whose
    /// free bond is being decreased
    /// @param amount The amount to decrease the free bond by
    function increaseFreeBond(address archaeologist, uint256 amount) internal {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // Increase the free bond amount
        s.archaeologistProfiles[archaeologist].freeBond += amount;
    }

    /// @notice Decreases the amount stored in the cursedBond mapping for an
    /// archaeologist. Reverts if the archaeologist's cursed bond is lower than
    /// the amount.
    /// @param archaeologist The address of the archaeologist whose
    /// cursed bond is being decreased
    /// @param amount The amount to decrease the cursed bond by
    function decreaseCursedBond(address archaeologist, uint256 amount) internal {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // Revert if the amount is greater than the current cursed bond
        if (amount > s.archaeologistProfiles[archaeologist].cursedBond) {
            revert LibErrors.NotEnoughCursedBond(
                s.archaeologistProfiles[archaeologist].cursedBond,
                amount
            );
        }

        // Decrease the cursed bond amount
        s.archaeologistProfiles[archaeologist].cursedBond -= amount;
    }

    /// @notice Increases the amount stored in the cursedBond mapping for an
    /// archaeologist.
    /// @param archaeologist The address of the archaeologist whose
    /// cursed bond is being decreased
    /// @param amount The amount to decrease the cursed bond by
    function increaseCursedBond(address archaeologist, uint256 amount) internal {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // Increase the cursed bond amount
        s.archaeologistProfiles[archaeologist].cursedBond += amount;
    }

    /// @notice Locks up the archaeologist's bond, decreasing the
    /// archaeologist's free bond by an amount and increasing the
    /// archaeologist's cursed bond by the same amount.
    /// @param archaeologist The address of the archaeologist
    /// @param amount The amount to lock up
    function lockUpBond(address archaeologist, uint256 amount) internal {
        // Decrease the free bond amount
        decreaseFreeBond(archaeologist, amount);

        // Increase the cursed bond amount
        increaseCursedBond(archaeologist, amount);
    }

    /// @notice Unlocks the archaeologist's bond, increasing the
    /// archaeologist's free bond by an amount and decreasing the
    /// archaeologist's cursed bond by the same amount.
    /// @param archaeologist The address of the archaeologist
    /// @param amount The amount to unlock
    function unlockBond(address archaeologist, uint256 amount) internal {
        // Decrease the cursed bond amount
        decreaseCursedBond(archaeologist, amount);

        // Increase the free bond amount
        increaseFreeBond(archaeologist, amount);
    }


    /// @notice Calculates an archaeologist's cursed bond and frees them
    /// (unlocks the cursed bond).
    /// @param sarcoId the identifier of the sarcophagus to free the archaeologist from
    /// @param archaeologistAddress the address of the archaeologist to free
    function freeArchaeologist(bytes32 sarcoId, address archaeologistAddress) internal {
        AppStorage storage s = LibAppStorage.getAppStorage();

        LibTypes.CursedArchaeologist storage cursedArchaeologist = s
            .sarcophagi[sarcoId]
            .cursedArchaeologists[archaeologistAddress];

        // Free up the archaeologist's locked bond
        unlockBond(archaeologistAddress, cursedArchaeologist.diggingFee);
    }
}

contracts/libraries/LibErrors.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

/**
 * @title A collection of Errors
 * @notice This library defines all of the Errors that the Sarcophagus system
 * uses.
 */
library LibErrors {
    error AccuseNotEnoughProof(uint256 shardsProvided, uint8 minShards);

    error AccuseIncorrectProof();

    error ArchaeologistAlreadyUnwrapped(address archaeologist);

    error ArchaeologistListNotUnique(address archaeologistAddress);

    error ArchaeologistNotOnSarcophagus(address archaeologist);

    error ArchaeologistProfileExistsShouldBe(bool exists, address archaeologist);

    error DiggingFeeTooLow(uint256 diggingFee, address archaeologist);

    error MinShardsGreaterThanArchaeologists(uint8 minShards);

    error MinShardsZero();

    error MaxResurrectionIntervalIsZero();

    error NewResurrectionTimeInPast(uint256 newResurrectionTime);

    error NewResurrectionTimeTooLarge(uint256 newResurrectionTime);

    error NoArchaeologistsProvided();

    error NotEnoughCursedBond(uint256 cursedBond, uint256 amount);

    error NotEnoughFreeBond(uint256 freeBond, uint256 amount);

    error NotEnoughReward(uint256 reward, uint256 amount);

    error ResurrectionTimeInPast(uint256 resurrectionTime);

    error ResurrectionTimeTooFarInFuture(
        uint256 resurrectionTime,
        uint256 sarcophagusMaximumRewrapInterval
    );

    error SarcophagusAlreadyExists(bytes32 sarcoId);

    error SarcophagusDoesNotExist(bytes32 sarcoId);

    error SarcophagusInactive(bytes32 sarcoId);

    error SarcophagusCompromised(bytes32 sarcoId);

    error SenderNotEmbalmer(address sender, address embalmer);

    error InvalidSignature(
        // address recovered from signature via ecrecover
        address recoveredAddress,
        // address we expected to have signed the data
        address expectedAddress
    );

    error SignerNotArchaeologistOnSarcophagus(bytes32 sarcoId, address signer);

    // Used when an attempt is made to accuse or rewrap after the resurrection time has already passed (so it's actually time to unwrap it)
    error SarcophagusIsUnwrappable();

    // Used when an attempt is made to clean a sarcophagus before the grace period after the resurrection time has passed
    error SarcophagusNotCleanable();

    error TooEarlyToUnwrap(uint256 resurrectionTime, uint256 currentTime);

    error TooLateToUnwrap(uint256 resurrectionTime, uint256 gracePeriod, uint256 currentTime);

    error UnencryptedShardHashMismatch(bytes rawKeyShare, bytes32 doubleHashedKeyShare);

    error SarcophagusParametersExpired(uint256 timestamp);
}

